import React from 'react'
import expandIcon from '../designes/expand.png'


const Design = ({design, onClick}) => {
    // const expand = () => (
    //     img.style.transform = "scale(1.5)"
    //     img.style.transition = "transform 0.25s ease"
    // )
    return (
        <div className='design'>
            <div className="overlay"> 
                <img src={expandIcon} onClick={() => onClick(design.id)}/>
            </div>
            <img src={design.image_path} className='thumbnail'/>
            <h3>{design.title}</h3>
            <h4>{design.author}</h4>
            <a href={design.design_path} download>click to download</a>
        </div>
    );
}

export default Design
