import React from 'react'
import Expanded from './Expanded';
import { useState } from "react"


const Design = ({image_path, design_path, author, title, expanded}) => {
    // const expand = () => (
    //     img.style.transform = "scale(1.5)"
    //     img.style.transition = "transform 0.25s ease"
    // )

    return (
        <div className='design'>
            <img src={image_path} className='thumbnail'/>
            <h3>{title}</h3>
            <h4>{author}</h4>
            <a href={design_path} download>click to download</a>
            <Expanded trigger={expanded} image_path={image_path}/>
        </div>
    );
}

export default Design
